import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class CounterButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = {count: 1};
  }

  shouldComponentUpdate(nextProps, nextState) {
    if (this.props.color !== nextProps.color) {
      return true;
    }
    if (this.state.count !== nextState.count) {
      return true;
    }
    return false;
  }

  render() {
    return (
      <button
        color={this.props.color}
        onClick={() => this.setState(state => ({count: state.count + 1}))}>
        Count: {this.state.count}
      </button>
    );
  }
}

function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}


class Board extends React.Component {
  renderSquare(i) {
    return (
    <Square key={i}
      value= {this.props.squares[i]} 
      onClick = { () => this.props.onClick(i) }
    /> );
  }

  render() {
    const animals = ["Dog", "Bird", "Cat", "Mouse", "Horse"];
    const getAnimalsContent = animals => {
      let content = [];
      for (let i = 0; i < animals.length; i++) {
        const item = animals[i];
        content.push(<li key={item.id}>{item.animal}</li>);
      }
      return content;
    };

    return <ul>{getAnimalsContent(animals)}</ul>;
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      history: [{
        squares: Array(9).fill(null),
      }],
      prevPos: [],
      stepNumber: 0,
      xIsNext: true,
    };
  }

  handleClick(i) {
    const history = this.state.history.slice(0, this.state.stepNumber + 1);
    const prevPos = this.state.prevPos.slice(0, this.state.stepNumber + 1);
    const current = history[history.length - 1];
    const squares = current.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      history: history.concat([{
        squares: squares
      }]),
      prevPos: prevPos.concat([i]),
      stepNumber: history.length,
      xIsNext: !this.state.xIsNext,
    });

    console.log(prevPos);
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: (step % 2) === 0,
    });
  }

  render() {
    const history = this.state.history;
    const current = history[this.state.stepNumber];
    const winner = calculateWinner(current.squares);
    const moves = history.map((step, move) => {
      let index = this.state.prevPos[move-1];
      const desc = move ?
        `Go to move ${move} (${index%3+1}, ${Math.floor(index/3)+1})`
        :
        'Go to game start';
      if (move === this.state.stepNumber) {
        return (
          <li key={move}>
            <button onClick={() => this.jumpTo(move)}><b>{desc}</b></button>
          </li>
        );
      }
      else { 
        return (
          <li key={move}>
            <button onClick={() => this.jumpTo(move)}>{desc}</button>
          </li>
        );
      }
    });
    let status;
    if (winner) {
      if (winner === "tie") { status = "Tie!" }
      else { status = `Winner:${winner}!`; }
    } else {
      status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
    }
    return (
      <div className="game">
        <div className="game-board">
          <Board
            squares={current.squares}
            onClick={(i) => this.handleClick(i)}
          />
        </div>
        <div className="game-info">
          <div>{status}</div>
          <ol>{moves}</ol>
        </div>
      </div>
    );
  }
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  if (isATie(squares)) { return "tie"; }
  return null;
}

  function isATie(squares) { 
    for (let i = 0; i < squares.length; i++) { 
      if (!squares[i]) {
        return false;
      }
    }
    return true;
  }

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
